		         join hd_professor_tb p on a.prof_id = p.prof_id
		         join HD_DEPARTMENT_TB d  on p.dept_code = d.dept_code
                 RIGHT JOIN (select R.STU_NO,r.sub_seq
		         from  hd_class_tb A
                 join hd_sub_req_tb r on a.sub_seq=r.sub_seq
